class ClientError(StandardError):
    """Represents an error in zetaindex Client.
    """

    def __init__(self, code_=0):
        StandardError.__init__(self)
        if code_ in RESPONSES:
            self.msg = RESPONSES[code_]
        else:
            self.msg = str(code_)
        self.code = code_

    def __str__(self):
        return "[%d] %s" % (self.code, self.msg)


RESPONSES = {
    100 : "Listing follows."
  , 200 : "Ok."
        }


